export type RequestHandler<I extends ConnectionType, O> = (
  connection: Connection<I>,
  response: I
) => O;

/** A function to handle an error. */
export type ErrorHandler = (
  connection: Connection<ConnectionType>,
  response: StorageError
) => StorageError;

/**
