/**
 * @fileoverview Enumerations used for upload tasks.
 */

/**
 * An event that is triggered on a task.
 * @internal
 */
export type TaskEvent = string;

/**
 * An event that is triggered on a task.
 * @internal
 */
export const TaskEvent = {
  /**
   * For this event,
   * <ul>
   *   <li>The `next` function is triggered on progress updates and when the
   *       task is paused/resumed with an `UploadTaskSnapshot` as the first
   *       argument.</li>
   *   <li>The `error` function is triggered if the upload is canceled or fails
   *       for another reason.</li>
   *   <li>The `complete` function is triggered if the upload completes
   *       successfully.</li>
   * </ul>
   */
  STATE_CHANGED: 'state_changed'
};

/**
 * Internal enum for task state.
 */
export const enum InternalTaskState {
  RUNNING = 'running',
  PAUSING = 'pausing',
  PAUSED = 'paused',
  SUCCESS = 'success',
  CANCELING = 'canceling',
  CANCELED = 'canceled',
  ERROR = 'error'
}

/**
 * Represents the current state of a running upload.
 * @internal
 */
export type TaskState = typeof TaskState[keyof typeof TaskState];

// type keys = keyof TaskState
/**
 * Represents the current state of a running upload.
 * @internal
 */
export const TaskState = {
  /** The task is currently transferring data. */
  RUNNING: 'running',

  /** The task was paused by the user. */
  PAUSED: 'paused',

  /** The task completed successfully. */
  SUCCESS: 'success',

  /** The task was canceled. */
  CANCELED: 'canceled',

  /** The task failed with an error. */
  ERROR: 'error'
} as const;

export function taskStateFromInternalTaskState(
  state: InternalTaskState
): TaskState {
  switch (state) {
    case InternalTaskState.RUNNING:
    case InternalTaskState.PAUSING:
    case InternalTaskState.CANCELING:
      return TaskState.RUNNING;
    case InternalTaskState.PAUSED:
      return TaskState.PAUSED;
    case InternalTaskState.SUCCESS:
      return TaskState.SUCCESS;
    case InternalTaskState.CANCELED:
      return TaskState.CANCELED;
    case InternalTaskState.ERROR:
      return TaskState.ERROR;
    default:
      // TODO(andysoto): assert(false);
      return TaskState.ERROR;
  }
}
